    private void btnPlayActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPlayActionPerformed
        // TODO add your handling code here:=
        if (!start)
        {
            btnInit();
            for (int i=0;i<9;i++)
                for (int j=0;j<9;j++)
                    arr[i][j].btn.setEnabled(true);
            
            for (int i=0;i<tb;i++)
            {
                int a=(int)(Math.random()*9);
                int b=(int)(Math.random()*9);
                if (!arr[a][b].bomb)
                    arr[a][b].activateBomb();
                else
                    i--;
            }//for loop
            
            for (int i=0;i<9;i++)
            {
                for (int j=0;j<9;j++)
                {
                    if (arr[i][j].bomb)
                    {
                        BombInc(i-1,j-1);
                        BombInc(i-1,j);
                        BombInc(i-1,j+1);
                        BombInc(i,j-1);
                        BombInc(i,j+1);
                        BombInc(i+1,j-1);
                        BombInc(i+1,j);
                        BombInc(i+1,j+1);
                    }
                }//for loop
            }//for loop
            t1=System.currentTimeMillis();
            start=true;
        }
        else {
            this.setVisible(false);
            MineWeeper game = new MineWeeper();
            game.tb=this.tb;
            game.setVisible(true);
        }
    }//GEN-LAST:event_btnPlayActionPerformed

    private void BombInc(int i,int j)
    {
        if (i<0||i>8||j<0||j>8)
            return ;
        arr[i][j].val++;
    }
    
