    private void btnPressed(int i,int j)
    {
        if (win||lose)
            return;
        if (!lose&&!win)
        {
        if (i<0||i>8||j<0||j>8)
            return;
        if (!arr[i][j].bomb&&!arr[i][j].visible) //not a bomb
        {
            nb++;
            if (nb+tb>=81)
            {
                win=true;
                if (t2==0)
                    t2=(System.currentTimeMillis()-t1)/1000;
            }
            arr[i][j].visible=true;
            if (arr[i][j].val!=0)
                arr[i][j].btn.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Graphics/Value"+arr[i][j].val+".png")));
            //arr[i][j].btn.setEnabled(true);
            if (arr[i][j].val==0)
            {
                arr[i][j].btn.setEnabled(false);
                btnPressed(i-1,j-1);
                btnPressed(i-1,j);
                btnPressed(i-1,j+1);
                btnPressed(i,j-1);
                btnPressed(i,j+1);
                btnPressed(i+1,j-1);
                btnPressed(i+1,j);
                btnPressed(i+1,j+1);
            }   
        }
        if (arr[i][j].bomb)
        {
            arr[i][j].btn.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Graphics/Bomb.png")));
            lose=true;
        }
        }
        if (win)
        {
            JOptionPane.showMessageDialog (null,"You Won : "+t2+" sec");
            scoreFeed();
        }
        if (lose)
            JOptionPane.showMessageDialog (null,"You Lose");
    }
    
    private void scoreFeed()
    {
        long high;
        try
        {
            FileReader fr = new FileReader("data.dat");
            BufferedReader br = new BufferedReader(fr);
            high=Long.valueOf(br.readLine());
            
        }
        catch (Exception e)
        {
            high=0;
        }
        if (high==0||high>t2)
        {
            try
            {
                FileWriter fw = new FileWriter("data.dat");
                BufferedWriter bw = new BufferedWriter(fw);
                PrintWriter p = new PrintWriter(bw);
                p.println(t2);
                p.flush();
                fw.close();
            }
            catch (Exception e)
            {
                ;
            } 
        }
    }
    
